changeSet.sql:
ALTER TABLE read_access OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE read_access TO ${db.liquibase_user};
GRANT SELECT, INSERT, DELETE ON TABLE read_access TO ${db.server_users_group};
CREATE INDEX read_access_id_type_index ON read_access USING btree (resource_id, access_type);
CREATE INDEX read_access_id_type_organization_index ON read_access USING btree (resource_id, access_type, organization_id);

ALTER TABLE current_all OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE current_all TO ${db.liquibase_user};
GRANT SELECT ON TABLE current_all TO ${db.server_users_group};

